val baseDir="https://git.cs.famaf.unc.edu.ar/dbarsotti/diplodatos_bigdata/raw/master/clases"

z.put("baseDir", baseDir)
print("""%html
<script>
    var heads = document.getElementsByTagName('h2');
    var numHeads = heads.length;
    var inner = "";
    var i = 0;
    var j = 0;
    while (i < numHeads){
        inner = heads[i].innerHTML;
        if (inner.search(".-") != -1 ) {
            j++;
            heads[i].innerHTML = inner.replace(/(~|\d+)\.-/,""+j+".-");
        }
        i++
    }
</script>
""")
